chrome.alarms.onAlarm.addListener((e=>{console.log("Alarm triggered:",e.name),chrome.notifications.create({type:"basic",iconUrl:"/assets/note.png",title:"Alarm Triggered!",message:`Alarm "${e.name}" fired at ${(new Date).toLocaleTimeString()}`,silent:!1}),chrome.tabs.create({url:"https://c4.wallpaperflare.com/wallpaper/853/914/400/clocks-suits-men-time-wallpaper-preview.jpg?alarmName=${encodeURIComponent(alarm.name)"})}));